using namespace std;

long long int largestproduct(void)
{
    long long int result=1,test;
    string number="";
    for(int i=0;i<(1000-12);i++){
        test=1;
        for(int j=0;j<13;j++){
            test*=((int)number[i+j]-48);
        }
        if(test>result)result=test;
        //cout<<"current result "<<result<<endl;
    }
    return result;
}

int main()
{
    long long int result=largestproduct();
    cout<<"largest 13 digits product is: "<<result<<endl;
    return 0;
}
